import React, {Component} from "react";

export default class Login extends Component {
  constructor() {
    super();

    this.state = {
      email: '',
      password: ''
    };
  }

  handleChange = event => {
    this.setState({
      [event.currentTarget.name]: event.target.value
    })
  }

  handleSubmit = event => {
    event.preventDefault()

    if (!this.state.username || !this.state.password) return

    this.props.onSubmit(this.state)
  }

  render() {
    return (
			<div class="container has-text-centered">
				<div class="column is-4 is-offset-4">
					<h3 class="title has-text-grey">Login</h3>
					<p class="subtitle has-text-grey">Please login to proceed.</p>
					<div class="box">
						<figure class="avatar">
							<img src="https://placehold.it/128x128"/>
						</figure>
						<form>
							<div class="field">
								<div class="control">
									<input id="email" name="email" class="input is-large" type="email" placeholder="Your Email" autofocus=""/>
								</div>
							</div>

							<div class="field">
								<div class="control">
									<input id="password" name="password" class="input is-large" type="password" placeholder="Your Password"/>
								</div>
							</div>
							<div class="field">
								<label class="checkbox">
									<input type="checkbox"/> Remember me
								</label>
							</div>
							<button type="submit" class="button is-block is-info is-large is-fullwidth">Login</button>
						</form>
					</div>
					<p class="has-text-grey">
						<a href="#">Sign Up</a> &nbsp;·&nbsp;
						<a href="#">Forgot Password</a> &nbsp;·&nbsp;
						<a href="#">Need Help?</a>
					</p>
				</div>
			</div>
    )
  }
}
