import React, {Component} from "react";

import '../css/Login.css';
import image from '../spotifly.png'

export default class Login extends Component {
  constructor() {
    super();

    this.state = {
      email: '',
      password: '',
      registerClicked: false
    };
  }

  handleChange = event => {
    this.setState({
      [event.currentTarget.name]: event.target.value
    })
  }

  handleSubmit = event => {
    event.preventDefault()
    let username = this.state.email
    let password = this.state.password

    debugger

    if (!username || !password){ 
    	return
    }else{
    	this.sendUserData(username,password)
    }

    //this.props.onSubmit(this.state)
  }

  sendUserData(username,password){
  	console.log(username, password, "is sending")

  }

  registerForm(){

  	console.log("Register was clicked")

  }
  renderRegister(){
  	return (
  		<div className="container has-text-centered">
				<div className="column is-4 is-offset-4">
					<h3 className="title has-text-grey">Login</h3>
					<p className="subtitle has-text-grey">Please Sign Up </p>
					<div className="box">
						<figure className="avatar" style={{width:"50%",height:"50%", margin:"auto"}}>
							<img src={image}/>
						</figure>
						<form onSubmit={this.handleSubmit}>
							<div className="field">
								<div className="control">
									<input id="email" name="email" className="input is-medium" type="email" onChange={this.handleChange} placeholder="Email" autofocus=""/>
								</div>
							</div>
							<div className="field">
								<div className="control">
									<input id="password" name="password" className="input is-medium" type="password" onChange={this.handleChange}  placeholder="Password"/>
								</div>
							</div>
							<div className="field">
								<label className="checkbox">
									<input type="checkbox"/> Remember me
								</label>
							</div>
							<button type="submit" className="button is-block is-info is-large is-fullwidth">Login</button>
						</form>
					</div>
					<p className="has-text-grey">
						<a href="#" onClick={this.registerForm}>Sign Up</a> &nbsp;路&nbsp;
						<a href="#">Forgot Password</a> &nbsp;路&nbsp;
						<a href="#">Need Help?</a>
					</p>
				</div>
			</div>
	)
  }

  renderLogin(){
  	return (
  		<div className="container has-text-centered">
				<div className="column is-4 is-offset-4">
					<h3 className="title has-text-grey">Login</h3>
					<p className="subtitle has-text-grey">Please login to proceed.</p>
					<div className="box">
						<figure className="avatar" style={{width:"50%",height:"50%", margin:"auto"}}>
							<img src={image}/>
						</figure>
						<form onSubmit={this.handleSubmit}>
							<div className="field">
								<div className="control">
									<input id="email" name="email" className="input is-medium" type="email" onChange={this.handleChange} placeholder="Email" autofocus=""/>
								</div>
							</div>
							<div className="field">
								<div className="control">
									<input id="password" name="password" className="input is-medium" type="password" onChange={this.handleChange}  placeholder="Password"/>
								</div>
							</div>
							<div className="field">
								<label className="checkbox">
									<input type="checkbox"/> Remember me
								</label>
							</div>
							<button type="submit" className="button is-block is-info is-large is-fullwidth">Login</button>
						</form>
					</div>
					<p className="has-text-grey">
						<a href="#" onClick={this.registerForm}>Sign Up</a> &nbsp;路&nbsp;
						<a href="#">Forgot Password</a> &nbsp;路&nbsp;
						<a href="#">Need Help?</a>
					</p>
				</div>
			</div>
)
  }


  renderForm(bool){
  	return !bool ? this.renderLogin() : this.renderRegister()
  }
  render() {
    return this.renderForm(this.state.registerClicked)


  }
}
